import './App.css';
import React, {useState} from 'react';
function App() {

  const [todos, setTodos] = useState(['Drink Coffee', 'Go for a Walk', 'Watch Netflix', 'Go Trekking']);
  const [input, setInput] = useState([]);
  console.log(input);

  const addTodo = (event) => {

    //while clicking the button 
    console.log('I am working');
    setTodos([...todos, input]); //pushing in the array

  }
  return (
    <div className="App">
      <h1>Hello React ! </h1>
      <form>
        <input value = {input} onChange = {event => setInput(event.target.value)}/>
        <button typeonClick = {addTodo}>Todo App</button>

      </form>

      <ul>{todos.map(todo => (<li>{todo}</li>))}</ul>

    </div>
  );
}

export default App;
